module.exports = function(){
    
    this.queue = require('pony-queue');
    
    this.before = function(callable){
        
        this.before = callable;
        return this;
    };
    
    this.after = function(callable){
        
        this.after = callable;
        return this;
    };
    
    this.callback = function(callable){
        
        this.callback = callable
        return this;
    };
};